@media (min-width: 1400px)
{
    .results
    {
        display: grid;
        grid-template-columns: 33.33% 33.33% 33.33% [end];
        justify-content: space-around;
    }
}

@media (min-width: 900px) and (max-width: 1400px)
{
    .results
    {
        display: grid;
        grid-template-columns: 50% 50% [end];
        justify-content: space-around;
    }
}

.result--sep, .result--more
{
    grid-column-start: 1;
    grid-column-end: end;
    width: 100%;
}

.result--ad
{
    opacity: 0.5;
}

.js-result-hidden-el, #organic-module
{
    display: none !important;
}

:root
{
    --font: "IBM Plex Serif", serif;
    --font-ui: "IBM Plex Sans", sans-serif;
    --font-monospace: "IBM Plex Mono", monospace;
    --font-size: 13px;

    --color-bg: #dfe4ea;
    --color-fg: #2f3542;

    --color-link: #5352ed;
    --color-bg-block: #f1f2f6;

    --color-accent: #747d8c;

    --para-indent: 1.6em;
}

body
{
    font-family: var(--font-ui);
    font-size: var(--font-size);
    background-color: var(--color-bg);
    color: var(--color-fg);
}

h1, h2, h3, h4, h5, h6
{
    font-family: unset;
    font-weight: unset;
    color: var(--color-accent);
    margin: 1.2em 0px;
}

a, #top-bar a, .content-panel .panel-footer a
{
    color: var(--color-link);
}

a:visited, #side-bar a:visited
{
    color: var(--color-link);
}

a:hover
{
    color: var(--color-bg);
    background-color: var(--color-link);
    text-decoration: none;
}

blockquote
{
    border: unset;
    background: unset;
    border-left: solid 10px var(--color-accent);
    margin: 1em 0;
    font-family: var(--font-monospace);
    font-size: 95%;
}

#header, #content-wrap
{
    margin: unset;
}

#header
{
    display: grid;
    grid-template-columns: 0.5fr 0.5fr;
    grid-template-rows: 55px 38px auto;
    background-position: 10px 0px;
    margin-top: 30px;
    margin-bottom: 30px;
}

#header h1
{
    grid-column-start: 1;
    grid-row-start: 1;
    float: unset;
}

#header h1 a
{
    padding: unset;
    line-height: unset;
    max-height: unset;
    font-size: 50px;
    text-shadow: unset;
    color: var(--color-fg);
}

#header h2
{
    grid-column-start: 1;
    grid-row-start: 2;
    float: unset;
}

#header h2 span
{
    font-size: 16px;
    text-shadow: unset;
    color: var(--color-accent);
}

#top-bar
{
    margin: 20px 0px;
    position: unset;
    height: unset;
    text-align: center;
    grid-column-start: 1;
    grid-column-end: span 2;
    grid-row-start: 3;
}

#top-bar ul
{
    float: unset;
}

#top-bar ul li
{
    display: inline-block;
    position: unset;
    float: unset;
}

#top-bar ul li a
{
    border: unset;
}

div#container-wrap
{
    background: unset;
}

#search-top-box
{
    grid-column-start: 2;
    grid-row-start: 2;
    position: unset;
    top: unset;
    right: unset;
    justify-self: end;
    align-self: center;
    width: unset;
}

#search-top-box input.empty
{
    color: unset;
}

#search-top-box-input, #search-top-box-form input[type="submit"]
{
    color: unset;
    background: unset;
}

#side-bar
{
    top: unset;
    left: unset;
    width: 300px;
}

#side-bar .side-block
{
    border: unset;
    border-radius: unset;
    box-shadow: unset;
    background: unset !important;
}

#side-bar .heading
{
    color: var(--color-accent);
    border: none;
}

div.scpnet-interwiki-wrapper
{
    display: none;
}

#main-content
{
    padding: unset;
    margin: unset;
    margin-left: 350px;
    font-family: var(--font);
    font-size: 18px;
}

.feature-block
{
    box-sizing: border-box;
}

.content-panel
{
    box-shadow: unset;
    border: unset;
    background-color: var(--color-bg-block);
    padding: 15px;
    box-sizing: border-box;
}

.content-panel .panel-heading, .content-panel .panel-body, .content-panel .panel-footer
{
    background: none;
    padding: unset;
}

.content-panel p
{
    padding: 0px;
}

.content-panel .panel-heading, .content-panel .panel-footer
{
    color: var(--color-accent);
    text-shadow: unset;
}

.content-panel.standalone
{
    background: unset;
}

.page-rate-widget-box
{
    font-size: 12px;
    font-family: var(--font-monospace);
    box-shadow: unset;
}

.page-rate-widget-box .rate-points, .page-rate-widget-box .cancel
{
    background-color: var(--color-accent) !important;
    border: solid 1px var(--color-accent);
    color: var(--color-bg);
}

.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown
{
    background-color: unset;
    border-top: solid 1px var(--color-accent);
    border-bottom: solid 1px var(--color-accent);
}

.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a
{
    color: var(--color-accent);
}

.page-rate-widget-box .cancel a
{
    color: var(--color-bg);
}

h1, #page-title
{
    color: var(--color-accent);
    font-family: var(--font-monospace);
    border: unset;
}

#main-content p
{
    text-indent: var(--para-indent);
    margin: unset;
}

#main-content h4 + p, #main-content div + p, #main-content p:first-child
{
    text-indent: 0px;
}

#main-content p > strong:first-child
{
    margin-left: calc(var(--para-indent) * -2); /* Why *2??? */
    margin-top: 0.6em;
    display: inline-block;
}

#main-content h4 + p > strong:first-child, #main-content div + p > strong:first-child, #main-content p:first-child > strong:first-child
{
    margin-left: unset;
}
