table.plhin
{
    margin: 20px 0px 20px 0px;
    background: white;
    border-radius: 10px;
    border: none;
}

.pi strong a
{
    border: none;
    color: #bdc3c7;
}

/* ========== Thread view ========================================> */

td.pls
{
    background: transparent;
    border: none;
}

td.pls > .favatar > div:nth-child(3)
{
    line-height: 0px;
}

td.pls div.avatar
{
    margin: 10px 0px 10px 10px;
}

td.pls div.avatar img
{
    width: 64px;
}

td.pls a.avtm
{
    border: none;
    box-shadow: none;
}

td.pls > .favatar > .xg2
{
    padding: 0px 0px 0px 10px;
    color: #bdc3c7;
}

.xi2, .xi2 a, .xi3 a
{
    color: inherit;
}

td.pls > div.pls > div > img
{
    display: none;
}

td.pls > div.pls > p, td.pls > div.pls > div.tip, td.pls > div.pls > dl, td.pls > div.pls > ul
{
    display: none;
}

td.plc .pi
{
    border: none;
}

td.plc div.authi
{
    display: none;
}

td.plc > div.po
{
    border: none;
}

.t_f, .t_f td
{
    font-size: inherit;
}

td.plc > div.pct
{
    font-size: 144%;
}

tr.ad
{
    display: none;
}

div#scrolltop
{
    display: none;
}

.quote
{
    color: inherit;
}

.ratl th, .ratl td, .ratc
{
    border: none;
}

/* ========== List view ==========================================> */

.tl tr:hover th, .tl tr:hover td
{
    background-color: inherit !important;
}

.tl th, .tl td
{
    border: none;
}

/* Seporator */
.tl .ts th, .tl .ts td
{
    background: #bdc3c7;
}

/* Head */
.tl .th
{
    border: none;
    background: #bdc3c7;
}

/* Tag list */
#thread_types
{
    display: none;
}

/* Category meta */
.pbn
{
    display: none;
}

/* “子版块” */
.bmw .bm_h
{
    border: none;
    border-top: none !important;
    background: #bdc3c7;
}

.tl td em
{
    color: inherit;
}

.tl td em a
{
    color: #2980b9;
}

.tl th a:visited, .tl td.fn a:visited
{
    color: #2980b9;
    text-decoration: none;
}

.tl th a:hover, .tl td.fn a:hover
{
    color: #ecf0f1;
    background-color: #2980b9;
    text-decoration: none;
}

.newthread tr th, .newthread tr td
{
    background: white;
}

.results
{
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: space-around;
}

.result
{
    max-width: 500px;
/*    margin-left: 1em;
    margin-right: 1em; */
}

.result__check
{
    margin-right: 0px;
}

.result--sep
{
    max-width: unset;
    width: 100%;
}
